import React, { Component } from "react";
import "./index.css";
import caretRight from "../assets/images/caret-right.jpg";
import caretDown from "../assets/images/caret-down.png";

class NodeItem extends Component{

    state={
        clickedNode : false
    }

    popUp=()=>{
        console.log('asfkad');
        this.setState({
            clickedNode: true
        })
    }

    render(){
        const { node, toggleExpansion, level } = this.props;
        const icon = node.isExpanded ? caretDown : caretRight;
        const style = node.isExpanded ? { marginRight: 13 } : null;
        const {clickedNode}= this.state;
        const textStyle = node.children.length > 0 ? null : { marginLeft: "28px" };
        return (
            <div className="node">
            {level > 0 && <span>|</span>}
            {node.children.length > 0 && (
                <img
                    hand
                    onClick={toggleExpansion}
                    style={style}
                    className="caret"
                    src={icon}
                    alt="caret"
                    height={14}
                ></img>
            )}
            <span style={textStyle} className="mx-10" onClick={this.popUp}>
                {node.title}
            </span>
            {clickedNode && (
                    <div style={{alignItems:'center' }}>
                        <div className="card" style={{width : '200px'}}>
                            <div className="card-body">
                                <h5 className="card-title">{node.title}</h5>
                                <button type="button" class="btn btn-outline-success">Add child</button>
                                <div>
                                     <button type="button" class="btn btn-outline-primary">Edit</button>

                                </div>
                            </div>
                        </div>
                    </div>


                )}
        </div>
        )
    }
}

export default NodeItem;
