import React from "react";
import "./index.css";
import Item from "./Component/Item";
import _ from "lodash";
import { connect } from "react-redux";

import {fetchTree, addNode} from './action/action';

// import Node from "./Component/Node";

const treeStructre = [
    {
        id: "101",
        title: "ABC",
        isExpanded: false,
        children: [
            {
                id: "102",
                title: "XYZ",
                isExpanded: false,
                children: [
                    {
                        id: "103",
                        title: "123",
                        isExpanded: false,
                        children: [
                            {
                                id : '104',
                                title: "456",
                                isExpanded: false,
                                children: []
                            },
                            {
                                id:'105',
                                title: "456",
                                isExpanded: false,
                                children: []
                            }
                        ]
                    },
                    {
                        id:'106',
                        title: "456",
                        isExpanded: false,
                        children: []
                    }
                ]
            },
            {
                id : '107',
                title: "EFG",
                isExpanded: false,
                children: []
            }
        ]
    },
    {
        id:'108',
        title: "DEF",
        isExpanded: false,
        children: []
    }
];

class App extends React.Component {
    state = {
        node: "",
        // tree:''
    };

    componentDidMount=()=>{
        const {getTree} = this.props;
        getTree(treeStructre);
    }

    handleChange = e => {
        this.setState({
            node: e.target.value
        });
    };

    add = () => {
        const { node } = this.state;
        const newNode = {
            id: this.generateUniqueId(),
            title: node,
            isExpanded: false,
            children: []
        };
        let newTree = _.cloneDeep(treeStructre);
        if (node.length) newTree.push(newNode);
        fetchTree(newNode);
    };

    generateUniqueId = () => {
        var result = "";
        var characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
        var charactersLength = characters.length;
        for (var i = 0; i < 3; i++) {
            result += characters.charAt(Math.floor(Math.random() * charactersLength));
        }
        return result;
    };

    render() {
        const { node } = this.state;
        const {tree} = this.props;
        return (
            <div className="container">
                <h3>Your Custom Storage</h3>
                <p>You can max use 4 characters/number for each level's name</p>
                <form className="form-inline my-2 my-lg-0">
                    <div className="d-flex align-items-flex-start">
                        <div>
                            <input
                                className="form-control mr-sm-2"
                                type="search"
                                maxLength={4}
                                placeholder="RM1"
                                aria-label="Search"
                                onChange={this.handleChange}
                            />
                            <small id="inputHelp" className="form-text text-muted">
                                Add top level (example: R1,RM1)
                            </small>
                        </div>
                        <button
                            className="btn btn-success my-2 my-sm-0"
                            type="submit"
                            onClick={this.add}
                            disabled={node.length === 0}
                        >
                            ADD
                        </button>
                    </div>
                </form>
                {tree.length > 0 && <Item tree={tree} />}
            </div>
        );
    }
}

const mapStateToProps = state => {
    const {structure} = state;
    console.log(structure);
    return { tree: structure };
  };

const mapDispatchToProps = dispatch => ({
    getTree: (tree) => {
      dispatch(fetchTree(tree));
    },
    appendNode: (tree) => {
        dispatch(addNode(tree));
      }
  }); 

export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(App);
