import React from "react";
import "./index.css";
import Item from "./Component/Item";
import _ from "lodash";
import { connect } from "react-redux";

// import Node from "./Component/Node";

const tree = [
    {
        id: "111",
        title: "ABC",
        isExpanded: false,
        children: [
            {
                id: "123",
                title: "XYZ",
                isExpanded: false,
                children: [
                    {
                        id: "456",
                        title: "123",
                        isExpanded: false,
                        children: [
                            {
                                title: "456",
                                isExpanded: false,
                                children: []
                            },
                            {
                                title: "456",
                                isExpanded: false,
                                children: []
                            }
                        ]
                    },
                    {
                        title: "456",
                        isExpanded: false,
                        children: []
                    }
                ]
            },
            {
                title: "EFG",
                isExpanded: false,
                children: []
            }
        ]
    },
    {
        title: "DEF",
        isExpanded: false,
        children: []
    }
];

class App extends React.Component {
    state = {
        node: "",
        tree
    };

    handleChange = e => {
        this.setState({
            node: e.target.value
        });
    };

    add = () => {
        const { node, tree } = this.state;
        const newNode = {
            id: this.generateUniqueId(),
            title: node,
            isExpanded: false,
            children: []
        };
        let newTree = _.cloneDeep(tree);
        if (node.length) newTree.push(newNode);
        this.setState({
            tree: newTree,
            node: ""
        });
    };

    generateUniqueId = () => {
        var result = "";
        var characters = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
        var charactersLength = characters.length;
        for (var i = 0; i < 3; i++) {
            result += characters.charAt(Math.floor(Math.random() * charactersLength));
        }
        return result;
    };

    render() {
        const { node, tree } = this.state;
        return (
            <div className="container">
                <h3>Your Custom Storage</h3>
                <p>You can max use 4 characters/number for each level's name</p>
                <form className="form-inline my-2 my-lg-0">
                    <div className="d-flex align-items-flex-start">
                        <div>
                            <input
                                className="form-control mr-sm-2"
                                type="search"
                                maxLength={4}
                                placeholder="RM1"
                                aria-label="Search"
                                onChange={this.handleChange}
                            />
                            <small id="inputHelp" className="form-text text-muted">
                                Add top level (example: R1,RM1)
                            </small>
                        </div>
                        <button
                            className="btn btn-success my-2 my-sm-0"
                            type="submit"
                            onClick={this.add}
                            disabled={node.length === 0}
                        >
                            ADD
                        </button>
                    </div>
                </form>
                <Item tree={tree} />
            </div>
        );
    }
}



export default connect(
    mapStateToProps,
    { fetchPosts }
  )(PostList);
