import React, { Component } from "react";
import "./index.css";
import caretRight from "../assets/images/caret-right.jpg";
import caretDown from "../assets/images/caret-down.png";

class NodeItem extends Component{

    state={
        clickedNode : false
    }

    popUp=()=>{
        this.setState({
            clickedNode: true
        })
    }

    render(){
        const { node, toggleExpansion, level } = this.props;
        const icon = node.isExpanded ? caretDown : caretRight;
        const style = node.isExpanded ? { marginRight: 13 } : null;
        const {clickedNode}= this.state;
        const textStyle = node.children.length > 0 ? null : { marginLeft: "28px" };
        return (
            <div className="node">
            {level > 0 && <span>|</span>}
            {node.children.length > 0 && (
                <img
                    hand
                    onClick={toggleExpansion}
                    style={style}
                    className="caret"
                    src={icon}
                    alt="caret"
                    height={14}
                ></img>
            )}
            <span style={textStyle} className="mx-10" onclick={this.popUp}>
                {node.title}
                {clickedNode && (
                    <div className="card" style={{width : '100px'}}>
                    <div className="card-body">
                      <h5 className="card-title">Card title</h5>
                      <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                    </div>
                  </div>

                )}
            </span>
        </div>
        )
    }
}

export default NodeItem;
